import react, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");

  const titleChangeHandler = (event) => {
    setEnteredTitle(event.target.value);
  };

  const amountChnageHandler = (event) => {
    setEnteredAmount(event.target.value);
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate), ///which will parse that date string and converted into a date object
    };

    props.onSaveExpenseData(expenseData);
    setEnteredTitle("");
    setEnteredAmount("");
    setEnteredDate("");
  };


  const beforeForm = <div className="new-expense__controls">
  <div className="new-expense__actions">
    <button type="button" onClick={showFormfun}>
      Add New Expense
    </button>
  </div>
</div>

const afterFrom = <form onSubmit={submitHandler}>
<div className="new-expense__controls">
  <div className="new-expense__control">
    <label>Title</label>
    <input
      type="text"
      value={enteredTitle}
      onChange={titleChangeHandler}
    />
  </div>
  <div className="new-expense__control">
    <label>Amount</label>
    <input
      type="number"
      min="0.01"
      step="0.01"
      value={enteredAmount}
      onChange={amountChnageHandler}
    />
  </div>
  <div className="new-expense__control">
    <label>Date</label>
    <input
      type="date"
      min="2019-01-01"
      max="2022-12-31"
      value={enteredDate}
      onChange={dateChangeHandler}
    />
  </div>
</div>
<div className="new-expense__actions">
  <button type="submit">Add Expense</button>
</div>
</form>







  return <div>{afterFrom}</div>;
};

export default ExpenseForm;
